export type Tkv = {
    [key: string]: any;
};
export type TdeclaredFamilyMap = {
    [key: string]: {
        selector: string[];
        path: string;
        chars: string;
    };
};
export type TfilterCallback = (declaredFamilyMap: TdeclaredFamilyMap) => void;
export type Toptions = {
    basePath: string;
    source?: string | string[];
    backup?: boolean;
    reserveText?: string | {
        [key: string]: string;
    };
    ignore?: string[];
    filter?: TfilterCallback;
    afterFilter?: TfilterCallback;
};
